hec.service('WflHandleService', [
    '$AuHttp',
    'HecService',
    function (auHttp, hecS) {

        return {
            /**
             * approve：工作流审批接口 : 同意、拒绝
             * 参数：数组
             * action_type：审批类型
             *              APPROVE    通过
             *              REJECT      拒绝
             * comment：审批意见
             * rcpt_id：工作流待办接受者ID
             */
            approve: approve,
            /**
             * commission：工作流转交接口
             * 参数：数组
             * rcpt_id：工作流待办接受者ID
             * commission_emp_id：转交员工ID
             * comment：转交意见
             */
            commission: commission,
            /**
             * add_approver：工作流添加审批人接口
             * 参数：数组
             * rcpt_id：工作流待办接受者ID
             * priority：添加顺序，之前[BEFORE]，平行[PARALLEL]，之后[AFTER]
             * comment：添加意见
             * add_emp_id：添加员工ID
             */
            addApprover: addApprover,
            /**
             * notify：工作流通知接口
             * 参数：数组
             * instance_id：工作流实例ID
             * node_id：工作流节点ID
             * notify_emp_id：通知员工ID
             * note：通知备注
             */
            notify: notify
        };

        function approve(options) {
            var para = [];
            if (options.length) {
                for (var i = 0; i < options.length; i++) {
                    options[i]._status = 'execute';
                }
                para = options;
            } else {
                options._status = 'execute';
                para.push(options);
            }
            return auHttp.request({
                url: hecS.getUrl('approve'),
                para: para
            });
        };


        function commission(option) {
            var para = [];
            if (option.length) {
                for (var i = 0; i < option.length; i++) {
                    option[i]._status = 'execute';
                }
                para = option;
            } else {
                option._status = 'execute';
                para.push(option);
            }
            return auHttp.request({
                url: hecS.getUrl('commission'),
                para: para
            });
        };

        /*
         [
         {
         rcpt_id: '',
         priority: '',
         emps: [
         {},{},{}
         ]
         }
         ]
         */
        function addApprover(option) {
            var para = [];
            if (option.length) {
                for (var i = 0; i < option.length; i++) {
                    option[i]._status = 'execute';
                }
                para = option;
            } else {
                option._status = 'execute';
                para.push(option);
            }
            return auHttp.request({
                url: hecS.getUrl('add_approver'),
                para: para
            });
        };

        function notify(option) {
            var para = [];
            if (option.length) {
                for (var i = 0; i < option.length; i++) {
                    option[i]._status = 'execute';
                }
                para = option;
            } else {
                option._status = 'execute';
                para.push(option);
            }
            return auHttp.request({
                url: hecS.getUrl('notify'),
                para: para
            });
        };

    }]);

